import React from 'react';

class Register extends React.Component {
	state = { registerEmail: '', 
						registerPassword: '', 
						registerName: '',
						user: {
							id: '',
							name: '',
							email: '',
							entries: 0,
							joined: ''
						}
					};						

	onRegisterSubmit = () => {
		const register = async () => {
			let res = await fetch('http://localhost:3001/register', {
				method: 'post',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					name: this.state.registerName,
					email: this.state.registerEmail,
					password: this.state.registerPassword
				})
			})
			res = await res.json();
			if (res) {
				this.props.loadUser(res);
				this.props.onRouteChange('home');		
			}
		}
		register();
	}

	render() {
		return (
			<article className="br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
				<main className="pa4 black-80">
				  <div className="measure">
				    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
				      <legend className="f1 fw6 ph0 mh0">Register</legend>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="email-address">Name</label>
				        <input 
					        onChange={e => this.setState({registerName: e.target.value})} 
					        className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        type="text" 
					        name="name"  
					        id="name"/>
				      </div>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
				        <input 
					        onChange={e => this.setState({registerEmail: e.target.value})}
					        className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        type="email" 
					        name="email-address"  
					        id="email-address"/>
				      </div>
				      <div className="mv3">
				        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
				        <input 
					        onChange={e => this.setState({registerPassword: e.target.value})}
					        className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        type="password" 
					        name="password"  
					        id="password"/>
				      </div>
				    </fieldset>
				    <div className="">
				      <input 
				      	onClick={this.onRegisterSubmit}
				      	className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
				      	type="submit" 
				      	value="Register"/>
				    </div>
				  </div>
				</main>
			</article>
		);
	}
}

export default Register;
